import React, { useCallback, useEffect, useMemo, useState } from "react";
import styled from "styled-components";
import { useWallet } from "use-wallet";
import { useTranslation } from "react-i18next";
import Button from "../../Button";
import Spacer from "../../Spacer";
import WalletModal from "../../WalletModal";
import useEncryptAddress from "../../../hooks/useEncryptAddress";
const Right: React.FC = () => {
  const { account, connect } = useWallet();
  const newAccount = useEncryptAddress(account);
  const [isOpen, setOpen] = useState(false);
  console.log("ðŸš€ ~ file: Right.tsx ~ line 9 ~ account", account);
  const { t } = useTranslation();

  useEffect(() => {
    if (!account) {
      connect("injected");
    }
  }, [account]);
  return (
    <StyledNavRight>
      <StyledWallet className="flex-row-center-center bd-bottom wing-blank-lg ">
        <Button
          text={!account ? t("ljqb") : newAccount}
          variant="secondary"
          onClick={() => {
            setOpen(true);
          }}
        />
      </StyledWallet>
      <div className="wing-blank-lg">
        <div className="bd-bottom">
          <StyledLabel>{t("jiage")}</StyledLabel>
          <div className="flex-jc-start">
            <div className="flex-jc-center">
              <img
                src={require("../../../assets/img/ETH_icon.png")}
                width="25"
                height="25"
              />
              <img
                src={require("../../../assets/img/USDT_icon.png")}
                width="25"
                height="25"
                className="margin-left-minus-10"
              />
            </div>
            <div className="margin-left-10">
              <div>1,200.45</div>
              <div>ETH/USDT</div>
            </div>
          </div>
          <Spacer size="md" />
          <div className="flex-jc-start">
            <div className="flex-jc-center">
              <img
                src={require("../../../assets/img/NEST_icon.png")}
                width="25"
                height="25"
              />
              <img
                src={require("../../../assets/img/USDT_icon.png")}
                width="25"
                height="25"
                className="margin-left-minus-10"
              />
            </div>
            <div className="margin-left-10">
              <div>1,200.45</div>
              <div>ETH/USDT</div>
            </div>
          </div>
          <Spacer size="md" />
          <div className="flex-jc-start">
            <div className="flex-jc-center">
              <img
                src={require("../../../assets/img/NEST_icon.png")}
                width="25"
                height="25"
              />
              <img
                src={require("../../../assets/img/ETH_icon.png")}
                width="25"
                height="25"
                className="margin-left-minus-10"
              />
            </div>
            <div className="margin-left-10">
              <div>1,200.45</div>
              <div>ETH/USDT</div>
            </div>
          </div>
          <Spacer size="md" />
        </div>
        <div className="">
          <StyledLabel>{t("ltl")}</StyledLabel>
          <div className="flex-jc-start">
            <div className="flex-jc-center">
              <img
                src={require("../../../assets/img/PETH_icon.png")}
                width="25"
                height="25"
              />
            </div>
            <div className="margin-left-10">
              <div>1,200.45</div>
              <div>PETH</div>
            </div>
          </div>
          <Spacer size="md" />
          <div className="flex-jc-start">
            <div className="flex-jc-center">
              <img
                src={require("../../../assets/img/PETH_icon.png")}
                width="25"
                height="25"
              />
            </div>
            <div className="margin-left-10">
              <div>1,200.45</div>
              <div>PUSD</div>
            </div>
          </div>
        </div>
      </div>
      <WalletModal
        isOpen={isOpen}
        onDismiss={() => {
          setOpen(false);
        }}
      />
    </StyledNavRight>
  );
};

const StyledNavRight = styled.div`
  background-color: ${(props) => props.theme.color.grey[100]};
