//@ts-nocheck
import React, { useCallback, useEffect, useMemo, useState } from "react";

import styled from "styled-components";
import { useTranslation } from "react-i18next";
import Modal from "../Modal";
import Spacer from "../Spacer";
import Button from "../Button";
// import {
//   useStatusModalToggle,
//   useStatusToggle,
//   useStatus,
//   useUpdateTransactionHash,
//   useTransactionHash
// } from './../state/application/hooks'
import {
  useWaitModal,
  useStatusModalToggle,
  useStatusToggle,
  useStatus,
  useTransactionHash
} from '../../state/application/hooks'
export default function StatusModal({}) {
  const { t } = useTranslation()
  const isOpen = useWaitModal()
  const onDismiss = useStatusModalToggle()
  const status = useStatus()
  const tx = useTransactionHash()

  const textList = ['qrz', 'jygbz', 'jycg', 'jysb', 'jyqx']
  const imgList = ['icon_loading', 'icon_broadcast', 'icon_success', 'icon_fail', 'icon_warning']
  return (
    <>
      <Modal
        isOpen={isOpen}
        onDismiss={() => {
          setOpen(false);
        }}
        width="300px"
      >
        <StyledModalHeader className="flex-jc-center">
          <CloseIcon
            src={require("../../assets/img/icon_close.png")}
            width="15"
            height="15"
            onClick={() => {
              setOpen(false);
            }}
          />
        </StyledModalHeader>

        <div className="text-center">
          <img
            src={require("../../assets/img/icon_warning.png")}
            width="48"
            height="48"
          />
          <Spacer size="ssm" />
          <div>{t('jyqx')}</div>
        </div>
        <Spacer size="mmd" />
        <Button variant="tertiary" text={t('ckjy')} width="100px" className="center-block"/>
      </Modal>
    </>
  );
}

const StyledModalHeader = styled.div`
  height: 40px;
  margin-top:-20px;
  justify-content: flex-end !important;
`;

const CloseIcon = styled.img`
  &:hover {
    cursor: pointer;
    opacity: 0.6;
  }
`;
