import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
const Nav: React.FC = () => {
  const { t } = useTranslation();
  return (
    <StyledNav>
      <StyledLink exact activeClassName="active" to="/">
        {t('shouye')}
      </StyledLink>
      <StyledLink exact activeClassName="active" to="/bank">
        {t('yinhang')}
      </StyledLink>
      <StyledLink exact activeClassName="active" to="/bonds">
        {t('zhaiquan')}
      </StyledLink>
      <StyledLink exact activeClassName="active" to="/boardroom">
        {t('huiyishi')}
      </StyledLink>
      <StyledLink2 href="https://snapshot.page/#/basiscash.eth" target="_blank">
        {t('jiaohuan')}
      </StyledLink2>
    </StyledNav>
  );
};

const StyledNav = styled.nav`
  align-items: center;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
`;

const StyledLink = styled(NavLink)`
  color: ${(props) => props.theme.color.grey[400]};

  padding-left: ${(props) => props.theme.spacing[3]}px;
  padding-right: ${(props) => props.theme.spacing[3]}px;
  text-decoration: none;
  &:hover {
    color: ${(props) => props.theme.color.grey[500]};
  }
  &.active {
    color: ${(props) => props.theme.color.primary.main};
  }
`;
const StyledLink2 = styled.a`
  color: ${(props) => props.theme.color.grey[400]};

  padding-left: ${(props) => props.theme.spacing[3]}px;
  padding-right: ${(props) => props.theme.spacing[3]}px;
  text-decoration: none;
  &:hover {
    color: ${(props) => props.theme.color.grey[500]};
  }
  &.active {
    color: ${(props) => props.theme.color.primary.main};
  }
`;

export default Nav;
