//@ts-nocheck
import React, { useCallback, useEffect, useMemo, useState } from "react";

import styled from "styled-components";
import { useTranslation } from "react-i18next";
import Modal from "../Modal";
import Spacer from "../Spacer";
import CardButton from "../CardButton";

export default function WalletModal({}) {
  const { t } = useTranslation();

  const [isOpen, setOpen] = useState(true);

  return (
    <>
      <Modal
        isOpen={isOpen}
        onDismiss={() => {
          setOpen(false);
        }}
        width="vw"
      >
        <StyledModalHeader className="flex-jc-center bd-bottom">
          <div>连接钱包</div>
          <CloseIcon
            src={require("../../assets/img/icon_close.png")}
            width="15"
            height="15"
            onClick={() => {
              onDismiss(false);
            }}
          />
        </StyledModalHeader>

        <Spacer size="mmd" />
        <CardButton className="width-100 wing-blank-lg">
          <Spacer size="sm" />
          <div className="flex-jc-start width-100">
            <img
              src={require("../../assets/img/metamask.png")}
              width="35"
              height="35"
              className="margin-right-10"
            />
            MetaMask
          </div>
          <Spacer size="sm" />
        </CardButton>
      </Modal>
    </>
  );
}

const StyledModalHeader = styled.div`
  height: 45px;
  margin-top: -20px;
`;
const CloseIcon = styled.img`
  &:hover {
    cursor: pointer;
    opacity: 0.6;
  }
`;
