//@ts-nocheck
import React from "react";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import TokenSymbol from "../../../components/TokenSymbol";
import Spacer from "../../../components/Spacer";
import Card from "../../../components/Card";
import Button from "../../../components/Button";
import Value from "../../../components/Value";
import useIsMobile from "../../../hooks/useIsMobile";
import useApprove from '../../../hooks/useApprove'
import useBasisCash from '../../../hooks/useBasisCash'
const Stake: React.FC = ({ mine, staked }) => {
  const isMobile = useIsMobile();
  const { t } = useTranslation();
  const basisCash = useBasisCash()

  const [approveStatus, approve] = useApprove(mine?.depositToken,basisCash?.contracts.Mine.address )
  return (
    <>
      <StyledWrapBox className={`wing-blank-lg ${isMobile ? "" : "width-47"} `}>
        <Spacer size="mmd" />
        <div className="color-grey">{t("wddy")}</div>
        <Spacer />
        <div className="wing-blank-llg  text-center">
          <div className="flex-row-center-center">
            <TokenSymbol symbol={mine.icon1} size={40} />
            <TokenSymbol symbol={mine.icon2} size={40} isRight={true} />
          </div>

          <Spacer size="sm" />
          <div className="font-size-24 bold-600">
            <Value value={staked} />
          </div>
          <Spacer size="sm" />
          <div className="color-grey">
            {t("ydy")} ( {mine.depositTokenName} )
          </div>
          <Spacer />
          <div className="flex-jc-center">
            <Button text={t("sq")} variant="secondary" width="47%" />
            <Button text={t("shuhui")} variant="tertiary" width="47%" />
          </div>
        </div>

        <Spacer size="mmd" />
      </StyledWrapBox>
    </>
  );
};

const StyledWrapBox = styled(Card)`
  height: 260px;
  @media (max-width: 768px) {
    height: auto;
  }
`;

export default Stake;
