import React, { useCallback, useEffect, useMemo, useState } from 'react';
import { useTranslation } from 'react-i18next';
import styled from 'styled-components';
import useIsMobile from '../../hooks/useIsMobile';
import Page from '../../components/Page';
import PageHeader from '../../components/PageHeader';
import Spacer from '../../components/Spacer';
import HomeCard from './components/HomeCard';
import { OverviewData } from './types';
import useBasisCash from '../../hooks/useBasisCash';
import config from '../../config';
import Notice from '../../components/Notice';
import farmer from '../../assets/img/farmer_md.png';

const Home: React.FC = () => {
  const isMobile = useIsMobile();
  const basisCash = useBasisCash();
  const { t } = useTranslation();
  const [{ cash, bond, share }, setStats] = useState<OverviewData>({});
  const fetchStats = useCallback(async () => {
    const [cash, bond, share] = await Promise.all([
      basisCash.getCashStatFromUniswap(),
      basisCash.getBondStat(),
      basisCash.getShareStat(),
    ]);
    if (Date.now() < config.bondLaunchesAt.getTime()) {
      bond.priceInDAI = '-';
    }
    setStats({ cash, bond, share });
  }, [basisCash, setStats]);

  useEffect(() => {
    if (basisCash) {
      fetchStats().catch((err) => console.error(err.stack));
    }
  }, [basisCash]);

  const cashAddr = useMemo(() => basisCash?.GAC.address, [basisCash]);
  const shareAddr = useMemo(() => basisCash?.GAS.address, [basisCash]);
  const bondAddr = useMemo(() => basisCash?.GAB.address, [basisCash]);

  return (
    <Page>
      <PageHeader
        icon={
          <img src={farmer} height={isMobile ? '65' : '131'} width={isMobile ? '130' : '261'} />
        }
        subtitle={t('mmybhgf')}
        title={t('hyld')}
      />
      <Spacer size={isMobile ? 'sm' : 'md'} />
      <CardWrapper>
        <HomeCard
          title={t('bizhong')}
          symbol="GAC"
          color="#EEA7ED"
          supplyLabel={'gongying'}
          address={cashAddr}
          stat={cash}
        />
        <Spacer size={isMobile ? 'sm' : 'md'} />
        <HomeCard
          title={t('fenxiang')}
          symbol="GAS"
          color="#DDEC5A"
          address={shareAddr}
          stat={share}
        />
        <Spacer size={isMobile ? 'sm' : 'md'} />
        <HomeCard
          title={t('geazhaiquan')}
          symbol="GAB"
          color="#FF9F31"
          address={bondAddr}
          stat={bond}
        />
      </CardWrapper>
    </Page>
  );
};

const StyledOverview = styled.div`
  align-items: center;
  display: flex;
  @media (max-width: 768px) {
    width: 100%;
    flex-flow: column nowrap;
    align-items: center;
  }
`;

const CardWrapper = styled.div`
  display: flex;
  justify-content: center;
  width: 100%;

  @media (max-width: 768px) {
    width: 100%;
    flex-flow: column nowrap;
    align-items: center;
  }
`;

const StyledNoticeContainer = styled.div`
  max-width: 768px;
  width: 90vw;
`;

const StyledSpacer = styled.div`
  height: ${(props) => props.theme.spacing[4]}px;
  width: ${(props) => props.theme.spacing[4]}px;
`;

const StyledLink = styled.a`

  text-decoration: none;
  color: ${(props) => props.theme.color.primary.main};
`;

export default Home;
