//@ts-nocheck
import { createReducer, nanoid } from '@reduxjs/toolkit'
import {


  toggleWalletModal,
  toggleSettingsMenu,
  updateBlockNumber,
  toggleWalletModal1,
  updateTransactionHash,
  toggleStatus
} from './actions'

type PopupList = Array<{ key: string; show: boolean; content: PopupContent; removeAfterMs: number | null }>

export interface ApplicationState {
  blockNumber: { [chainId: number]: number }
  walletModalOpen: boolean
  settingsMenuOpen: boolean
}

const initialState: ApplicationState = {
  blockNumber: {},
  walletModalOpen: false,
  walletModalOpen1: false,
  status: 1,
  settingsMenuOpen: false,
  transactionHash:''
}

export default createReducer(initialState, builder =>
  builder
    .addCase(updateBlockNumber, (state, action) => {
      const { chainId, blockNumber } = action.payload
      if (typeof state.blockNumber[chainId] !== 'number') {
        state.blockNumber[chainId] = blockNumber
      } else {
        state.blockNumber[chainId] = Math.max(blockNumber, state.blockNumber[chainId])
      }
    })
    .addCase(toggleWalletModal, state => {
      state.walletModalOpen = !state.walletModalOpen
    })
    .addCase(toggleSettingsMenu, state => {
      state.settingsMenuOpen = !state.settingsMenuOpen
    })
    .addCase(toggleWalletModal1, (state, action) => {
      state.walletModalOpen1 =
        action.payload.walletModalOpen1 !== undefined ? action.payload.walletModalOpen1 : !state.walletModalOpen1
    })
    .addCase(toggleStatus, (state, action) => {
      state.status = action.payload.status !== undefined ? action.payload.status : 1
    })
    .addCase(updateTransactionHash, (state, action) => {
      state.transactionHash =
        action.payload.transactionHash !== undefined ? action.payload.transactionHash : ''
    })

    .addCase(addPopup, (state, { payload: { content, key, removeAfterMs = 15000 } }) => {
      state.popupList = (key ? state.popupList.filter(popup => popup.key !== key) : state.popupList).concat([
        {
          key: key || nanoid(),
          show: true,
          content,
          removeAfterMs
        }
      ])
    })
    .addCase(removePopup, (state, { payload: { key } }) => {
      state.popupList.forEach(p => {
        if (p.key === key) {
          p.show = false
        }
      })
    })
)
