import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import Container from '../Container';
import Logo from '../Logo';
import useIsMobile from '../../hooks/useIsMobile';
import { NavLink } from 'react-router-dom';
import AccountButton from './components/AccountButton';
import Nav from './components/Nav';
import TxButton from './components/TxButton';
import { useTranslation } from 'react-i18next';
const TopBar: React.FC = () => {
  const [show, setShow] = useState(false);
  const toggleShow = useCallback(() => {
    setShow(!show);
  }, [show]);
  const { t, i18n } = useTranslation();
  const changeLanguage = useCallback(
    (lang) => {
      localStorage.setItem('lang', lang);
      i18n.changeLanguage(lang);
    },
    [i18n.language],
  );

  const isMobile = useIsMobile();
  return (
    <StyledTopBar>
      <Container size="lg">
        <StyledTopBarInner>
          {!isMobile ? (
            <>
              <div style={{ flex: 1 }}>
                <Logo />
              </div>
              <Nav />
              <StyledRightWrap>
                <TxButton />
                <AccountButton />
              </StyledRightWrap>
            </>
          ) : (
            <>
              <div className="flex1 flex-jc-center" style={{ padding: '10px 0' }}>
                <Logo />
                <StyledRightWrap>
                  <TxButton />
                  <AccountButton />
                  <img
                    src={require('../../assets/img/menu.png')}
                    width="20"
                    height="16"
                    className="margin-left-4"
                    onClick={toggleShow}
                  />
                  {show ? (
                    <div>
                      <StyledNavMask onClick={toggleShow}></StyledNavMask>
                      <StyledNavBox onClick={toggleShow}>
                        <StyledLink exact activeClassName="active" to="/">
                          {t('shouye')}
                        </StyledLink>
                        <StyledLink exact activeClassName="active" to="/bank">
                          {t('yinhang')}
                        </StyledLink>
                        <StyledLink exact activeClassName="active" to="/bonds">
                          {t('zhaiquan')}
                        </StyledLink>
                        <StyledLink exact activeClassName="active" to="/boardroom">
                          {t('huiyishi')}
                        </StyledLink>
                        <StyledLink2
                          href="https://snapshot.page/#/basiscash.eth"
                          target="_blank"
                        >
                          {t('jiaohuan')}
                        </StyledLink2>
                        <StyledLang as="div">
                          <span
                            className={i18n.language === 'zh' ? 'active' : ''}
                            onClick={() => {
                              changeLanguage('zh');
                            }}
                          >
                            {t('zhongwen')}
                          </span>
                          /
                          <span
                            className={i18n.language === 'en' ? 'active' : ''}
                            onClick={() => {
                              changeLanguage('en');
                            }}
                          >
                            {t('yingwen')}
                          </span>
                        </StyledLang>
                      </StyledNavBox>
                    </div>
                  ) : null}
                </StyledRightWrap>
              </div>
            </>
          )}
        </StyledTopBarInner>
      </Container>
    </StyledTopBar>
  );
};

const StyledTopBar = styled.div``;

const StyledTopBarInner = styled.div`
  align-items: center;
  display: flex;
  height: ${(props) => props.theme.topBarSize}px;
  justify-content: space-between;
  max-width: ${(props) => props.theme.siteWidth}px;
  width: 100%;
  flex-wrap: wrap;
  @media (max-width: 768px) {
    height: auto;
  }
`;

const StyledRightWrap = styled.div`
  flex: 1;
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;
const StyledNavMask = styled.div`
  position: fixed;
  top: 50px;
  bottom: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.4);
  height: 100%;
  width: 100%;
  z-index: 777;
  font-size: 13px;
`;
const StyledNavBox = styled.div`
  position: fixed;
  width: 175px;
  right: 15px;
  top: 49px;
  z-index: 888;
  line-height: 45px;
  border-radius: 10px;
  background: ${(props) => props.theme.color.grey[1000]};
  text-align: center;
`;
const StyledLink2 = styled.a`
  text-decoration: none;
  color: ${(props) => props.theme.color.grey[400]};
`;

const StyledLink = styled(NavLink)`
  color: ${(props) => props.theme.color.grey[400]};
  text-decoration: none;
  display: block;

  &.active {
    color: ${(props) => props.theme.color.primary.main};
  }
`;

const StyledLang = styled(StyledLink)`
  border-top: 0.5px solid rgba(76, 76, 76, 0.2);
  & .active {
    color: ${(props) => props.theme.color.primary.main};
  }
`;
export default TopBar;
