//@ts-nocheck
import { useCallback, useMemo } from "react";
import {

  PopupContent,
  removePopup,
  toggleWalletModal1,
  toggleWalletModal,
  toggleStatus,
  updateTransactionHash,
  toggleSettingsMenu,
} from "./actions";
import { useSelector, useDispatch } from "react-redux";
import { AppState } from "../index";
import { useWallet } from "use-wallet";

export function useBlockNumber(): number | undefined {
  const { chainId } = useWallet();
  return useSelector(
    (state: AppState) => state.application.blockNumber[chainId ?? -1]
  );
}

export function useWalletModalOpen(): boolean {
  return useSelector((state: AppState) => state.application.walletModalOpen);
}

export function useWalletModalToggle(): () => void {
  const dispatch = useDispatch();
  return useCallback(() => dispatch(toggleWalletModal()), [dispatch]);
}

export function useSettingsMenuOpen(): boolean {
  return useSelector((state: AppState) => state.application.settingsMenuOpen);
}

export function useToggleSettingsMenu(): () => void {
  const dispatch = useDispatch();
  return useCallback(() => dispatch(toggleSettingsMenu()), [dispatch]);
}

// returns a function that allows adding a popup
export function useAddPopup(): (content: PopupContent, key?: string) => void {
  const dispatch = useDispatch();

  return useCallback(
    (content: PopupContent, key?: string) => {
      dispatch(addPopup({ content, key }));
    },
    [dispatch]
  );
}

// returns a function that allows removing a popup via its key
export function useRemovePopup(): (key: string) => void {
  const dispatch = useDispatch();
  return useCallback(
    (key: string) => {
      dispatch(removePopup({ key }));
    },
    [dispatch]
  );
}

// get the list of active popups
export function useActivePopups(): AppState["application"]["popupList"] {
  const list = useSelector((state: AppState) => state.application.popupList);
  return useMemo(() => list.filter((item) => item.show), [list]);
}
export function useWalletModalToggle1() {
  const dispatch = useDispatch();
  return useCallback(
    (walletModalOpen1) => dispatch(toggleWalletModal1({ walletModalOpen1 })),
    [dispatch]
  );
}

export function useWaitModal() {
  return useSelector((state) => {
    return state.application.walletModalOpen1;
  });
}

export function useUpdateTransactionHash() {
  const dispatch = useDispatch();
  return useCallback(
    (transactionHash) => dispatch(updateTransactionHash({ transactionHash })),
    [dispatch]
  );
}

export function useTransactionHash() {
  return useSelector((state) => {
    return state.application.transactionHash;
  });
}
export function useStatusToggle() {
  const dispatch = useDispatch();
  return useCallback((status) => dispatch(toggleStatus({ status })), [
    dispatch,
  ]);
}
export function useStatus() {
  return useSelector((state) => {
    return state.application.status;
  });
}
