import React, { useRef, useContext, useCallback, useState } from "react";
import { useTranslation } from "react-i18next";
import useIsMobile from "../../../hooks/useIsMobile";
const Lang: React.FC = () => {
  const isMobile = useIsMobile();
  const { t, i18n } = useTranslation();
  const changeLanguage = useCallback(
    (lang) => {
      localStorage.setItem("lang", lang);
      i18n.changeLanguage(lang);
    },
    [i18n.language]
  );
  return (
    <div className="wing-blank-lg ">
      <div className={` ${isMobile ? "" : "bd-bottom width-100"} `}>
        <div className="space-white-lg"></div>
        <div
          className={`wing-blank  ${
            isMobile ? "flex-row-center-center" : "flex-jc-start  flex-wrap"
          } `}
        >
          <div
            className={`${ i18n.language === 'en' ? 'opacity-6' : ''} flex-row-center-center width-33`}
            onClick={() => {
              changeLanguage("en");
            }}
          >
            <img
              src={require("../../../assets/img/EN.png")}
              width="30"
              height="30"
            />
          </div>
          <div
            className={`${ i18n.language === 'zh' ? 'opacity-6' : ''} flex-row-center-center width-33`}
            onClick={() => {
              changeLanguage("zh");
            }}
          >
            <img
              src={require("../../../assets/img/CN.png")}
              width="30"
              height="30"
            />
          </div>
        </div>
        <div className="space-white-lg"></div>
      </div>
    </div>
  );
};

export default Lang;
