//@ts-nocheck
import React, { useState, useCallback } from "react";
import styled from "styled-components";
import { NavLink } from 'react-router-dom';
import { useTranslation } from "react-i18next";
import Spacer from "../../Spacer";
import useIsMobile from "../../../hooks/useIsMobile";
const Nav: React.FC = () => {
  const isMobile = useIsMobile();
  const { t } = useTranslation();
  const handleActive = useCallback((match, location) => {
    const { pathname } = location
    if (pathname.includes('coin')) {
      return true
    }
  }, [])
  return (
    <div className="wing-blank-lg ">
      <div className={` ${isMobile ? "" : "bd-bottom width-100"} `}>
        <div className="wing-blank">
          <StyledBarItem className="flex-jc-start" exact activeClassName="active" to="/"  >
            <img
              src={require("../../../assets/img/home_icon.png")}
              width="35"
              height="35"
              className="margin-right-5"
            />
            {t('shouye')}
          </StyledBarItem>
          <StyledBarItem className="flex-jc-start" exact activeClassName="active" to="/coin" isActive={(match, location)=>{

            handleActive(match, location)
          }}>
            <img
              src={require("../../../assets/img/coin_icon.png")}
              width="35"
              height="35"
              className="margin-right-5"
            />
             {t('zhubi')}
          </StyledBarItem>
          <StyledBarItem className="flex-jc-start" exact activeClassName="active" to="/exchange">
            <img
              src={require("../../../assets/img/exchange_icon.png")}
              width="35"
              height="35"
              className="margin-right-5"
            />
             {t('duihuan')}
          </StyledBarItem>
          <StyledBarItem className="flex-jc-start" exact activeClassName="active" to="/itank">
            <img
              src={require("../../../assets/img/pool_icon.png")}
              width="35"
              height="35"
              className="margin-right-5"
            />
            {t('bxc')}
          </StyledBarItem>
          <StyledBarItem className="flex-jc-start " exact activeClassName="active" to="/mine">
            <img
              src={require("../../../assets/img/mine_icon.png")}
              width="35"
              height="35"
              className="margin-right-5"
            />
            {t('wakuang')}
          </StyledBarItem>
        </div>
      </div>
    </div>
  );
};


const StyledBarItem = styled(NavLink)`
  color: ${(props) => props.theme.color.grey[300]};
  padding-top: 10px;
  &.active  {
    color: ${(props) => props.theme.color.primary.main}!important;
    box-sizing: border-box;
  }
  &:last-child {
    padding-bottom: 10px;
  }
`;

export default Nav;
