import React, { useEffect } from 'react';
import styled from 'styled-components';
import useIsMobile from '../../hooks/useIsMobile';
import { useParams } from 'react-router-dom';
import { useWallet } from 'use-wallet';
import bankImg from '../../assets/img/bank.png';
import swap_logo from '../../assets/img/swap_logo.png';
import Button from '../../components/Button';
import PageHeader from '../../components/PageHeader';
import Spacer from '../../components/Spacer';
import Harvest from './components/Harvest';
import Stake from './components/Stake';
import useBank from '../../hooks/useBank';
import useRedeem from '../../hooks/useRedeem';
import { Bank as BankEntity } from '../../basis-cash';
import { useTranslation } from 'react-i18next';
const Bank: React.FC = () => {
  // useEffect(() => window.scrollTo(0, 0));
  //@ts-ignore
  const { bankId } = useParams();
  const bank = useBank(bankId);
  const isMobile = useIsMobile();
  const { account } = useWallet();
  const { onRedeem } = useRedeem(bank);
  const { t } = useTranslation();
  return account && bank ? (
    <>
      <PageHeader
        icon={
          <img src={bankImg} height={isMobile ? '72' : '163'} width={isMobile ? '81' : '145'} />
        }
        subtitle={t('crbzq', {
          label: bank?.depositTokenName,
          label1: bank?.earnTokenName,
        })}
        title={bank?.name}
      />
      <Spacer size="md" />
      <StyledBank>
        <StyledCardsWrapper>
          <StyledCardWrapper>
            <Harvest bank={bank} />
          </StyledCardWrapper>
          <Spacer />
          <StyledCardWrapper>
            <Stake bank={bank} />
          </StyledCardWrapper>
        </StyledCardsWrapper>
        <Spacer size="lg" />
        {bank.depositTokenName.includes('LP') && <LPTokenHelpText bank={bank} />}
        <Spacer size="lg" />
        <div>
          <Button onClick={onRedeem} text={t('jsbtq')} />
        </div>
        <Spacer size="lg" />
      </StyledBank>
    </>
  ) : !bank ? (
    <BankNotFound />
  ) : (
    <UnlockWallet />
  );
};

const LPTokenHelpText: React.FC<{ bank: BankEntity }> = ({ bank }) => {
  let pairName: string;
  let uniswapUrl: string;

  const { t } = useTranslation();
  if (bank.depositTokenName.includes('GAC')) {
    pairName = 'GAC-DAI pair';
    uniswapUrl =
      'https://app.uniswap.org/#/add/0x3449FC1Cd036255BA1EB19d65fF4BA2b8903A69a/0x6B175474E89094C44Da98b954EedeAC495271d0F';
  } else {
    pairName = 'GAS-DAI pair';
    uniswapUrl =
      'https://app.uniswap.org/#/add/0xa7ED29B253D8B4E3109ce07c80fc570f81B63696/0x6B175474E89094C44Da98b954EedeAC495271d0F';
  }
  return (
    <StyledLink href={uniswapUrl} target="_blank">
      <img src={swap_logo} width="30" height="30" className="margin-right-5" />{' '}
      {t('zzxtgldx', {
        label: pairName,
      })}
      <img src={swap_logo} width="30" height="30" className="margin-left-5" />
    </StyledLink>
  );
};

const BankNotFound = () => {
  const { t } = useTranslation();
  return (
    <Center>
      <PageHeader icon="🏚" title={t('wzd')} subtitle={t('nzdyh')} />
    </Center>
  );
};

const UnlockWallet = () => {
  const { connect } = useWallet();
  const { t } = useTranslation();
  return (
    <Center>
      <Button onClick={() => connect('injected')} text={t('jsqb')} />
    </Center>
  );
};

const StyledBank = styled.div`
  align-items: center;
  display: flex;
  flex-direction: column;
  @media (max-width: 768px) {
    width: 100%;
  }
`;

const StyledUniswapLPGuide = styled.div`
  margin: -24px auto 48px;
`;

const StyledLink = styled.a`

  text-decoration: none;
  color: ${(props) => props.theme.color.primary.main};
  display: flex;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  padding: 0 15px;
  box-sizing: border-box;
`;

const StyledCardsWrapper = styled.div`
  display: flex;
  width: 600px;
  @media (max-width: 768px) {
    width: 100%;
    flex-flow: column nowrap;
    align-items: center;
  }
`;

const StyledCardWrapper = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  @media (max-width: 768px) {
    width: 80%;
  }
`;

const Center = styled.div`
  display: flex;
  flex: 1;
  align-items: center;
  justify-content: center;
`;

export default Bank;
