import React from "react";
import styled from "styled-components";
import Logo from "../../Logo";
const Header: React.FC = () => {
  const [tabs, setTabs] = useState([
    {
      text: "铸币",
      id: 1,
    },
    {
      text: "我的债仓",
      id: 2,
    },
  ]);
  return (
    <>
      <StyledHeader className="bd-bottom color-grey wing-blank-lg flex-jc-start">
        <StyledHeaderText>
          <div>Stake Your crypto assets</div>
          <div> to mint parallel assets</div>
        </StyledHeaderText>
        <img
          src={require("../../../assets/img/icon_menu.png")}
          width="28"
          height="28"
        />
        <Logo />
        <div></div>
      </StyledHeader>

      <StyledMask></StyledMask>
      <StyledSidebar>
      <Tab tabs={tabs} tab={tab} onChangeTab={() => {}} />
      </StyledSidebar>
    </>
  );
};
const StyledHeader = styled.div`
  height: 90px;
  @media (max-width: 768px) {
    height: 50px;
    justify-content: space-between;
  }
`;
const StyledMask = styled.div`
  position: fixed;
  height: calc(100vh - 50px);
  background: rgba(0, 0, 0, 0.45);
  top: 50px;
  left: 0;
  width: 100%;
  z-index: 8888;
`;
const StyledSidebar = styled.div`
  background: #fff;
  position: fixed;
  height: calc(100vh - 50px);
  text-align: center;
  top: 50px;
  /* left: -2.1rem; */
  left: 0;
  width: 2.1rem;
  z-index: 9999;
  transition: all 0.2s ease-in-out;
`;

const StyledHeaderText = styled.div`
  @media (max-width: 768px) {
    display: none;
  }
`;
export default Header;
