//@ts-nocheck
import React, { useCallback, useEffect, useMemo, useState } from "react";
import styled from "styled-components";
import Logo from "../../Logo";
import Tab from "../../Tab";
import Spacer from "../../Spacer";
import Right from "./Right";
const Header: React.FC = () => {
  const [tabs, setTabs] = useState([
    {
      text: "菜单",
      id: 1,
    },
    {
      text: "数据",
      id: 2,
    },
  ]);

  const [tab, setTab] = useState(1);
  return (
    <>
      <StyledHeader className="bd-bottom color-grey wing-blank-lg flex-jc-start">
        <StyledHeaderText>
          <div>Stake Your crypto assets</div>
          <div> to mint parallel assets</div>
        </StyledHeaderText>
        <img
          src={require("../../../assets/img/icon_menu.png")}
          width="28"
          height="28"
        />
        <Logo />
        <div></div>
      </StyledHeader>

      <StyledMask></StyledMask>
      <StyledSidebar>
        <div className="wing-blank-lg">
          <Spacer size="mmd" />
          <Tab tabs={tabs} tab={tab} onChangeTab={() => {}} />
          <Spacer size="mmd" />
          <div>
            <StyledLabel>我的平行资产</StyledLabel>
            <div className="flex-jc-center">
              <div className="flex-jc-start">
                <img
                  src={require(`../../../assets/img/PETH_icon.png`)}
                  width="35"
                  height="35"
                  className="margin-right-10"
                />
                <div>
                  <div>PETH</div>
                  <Spacer size="ssm" />
                  <div className="flex-jc-start color-grey">
                    <div>234445678  </div>
                    <img
                      src={require("../../../assets/img/copy_icon.png")}
                      width="16"
                      height="16"
                      className="margin-left-10"
                    />
                  </div>
                  <div>
                  20.4756
                  </div>
                </div>
              </div>

            </div>
            <Spacer size="mmd" />
            <div>
              <StyledLabel>价格</StyledLabel>
              <div className="flex-jc-start">
                <div className="flex-jc-center">
                  <img
                    src={require("../../../assets/img/ETH_icon.png")}
                    width="25"
                    height="25"
                  />
                  <img
                    src={require("../../../assets/img/USDT_icon.png")}
                    width="25"
                    height="25"
                    className="margin-left-minus-10"
                  />
                </div>
                <div className="margin-left-10">
                  <div>1,200.45</div>
                  <div>ETH/USDT</div>
                </div>
              </div>
              <Spacer size="mmd" />
              <div className="flex-jc-start">
                <div className="flex-jc-center">
                  <img
                    src={require("../../../assets/img/NEST_icon.png")}
                    width="25"
                    height="25"
                  />
                  <img
                    src={require("../../../assets/img/USDT_icon.png")}
                    width="25"
                    height="25"
                    className="margin-left-minus-10"
                  />
                </div>
                <div className="margin-left-10">
                  <div>1,200.45</div>
                  <div>ETH/USDT</div>
                </div>
              </div>
              <Spacer size="mmd" />
              <div className="flex-jc-start">
                <div className="flex-jc-center">
                  <img
                    src={require("../../../assets/img/NEST_icon.png")}
                    width="25"
                    height="25"
                  />
                  <img
                    src={require("../../../assets/img/ETH_icon.png")}
                    width="25"
                    height="25"
                    className="margin-left-minus-10"
                  />
                </div>
                <div className="margin-left-10">
                  <div>1,200.45</div>
                  <div>ETH/USDT</div>
                </div>
              </div>
              <Spacer size="mmd" />
            </div>

            <div className="">
              <StyledLabel>流通量</StyledLabel>
              <div className="flex-jc-start">
                <div className="flex-jc-center">
                  <img
                    src={require("../../../assets/img/PETH_icon.png")}
                    width="25"
                    height="25"
                  />
                </div>
                <div className="margin-left-10">
                  <div>1,200.45</div>
                  <div>PETH</div>
                </div>
              </div>
              <Spacer size="mmd" />
              <div className="flex-jc-start">
                <div className="flex-jc-center">
                  <img
                    src={require("../../../assets/img/PUSDT_icon.png")}
                    width="25"
                    height="25"
                  />
                </div>
                <div className="margin-left-10">
                  <div>1,200.45</div>
                  <div>PUSD</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </StyledSidebar>
    </>
  );
};
const StyledHeader = styled.div`
  height: 90px;
  @media (max-width: 768px) {
    height: 50px;
    justify-content: space-between;
  }
`;
const StyledMask = styled.div`
  position: fixed;
  height: calc(100vh - 51px);
  background: rgba(0, 0, 0, 0.45);
  top: 51px;
  left: 0;
  width: 100%;
  z-index: 8888;
`;
const StyledSidebar = styled.div`
  background: #fff;
  position: fixed;
  height: calc(100vh - 51px);
  top: 51px;
  /* left: -220px; */
  left: 0;
  width: 220px;
  z-index: 9999;
  transition: all 0.2s ease-in-out;
`;

const StyledHeaderText = styled.div`
  @media (max-width: 768px) {
    display: none;
  }
`;
const StyledLabel = styled.div`
  height: 35px;
  display: flex;
  align-items: center;
`;
export default Header;
