import { useCallback, useEffect, useState } from "react";
import BigNumber1 from "bignumber.js";
import useBasisCash from "./useBasisCash";
import { useBlockNumber } from "../state/application/hooks";

const useMineInfo = (mine) => {
  const [staked, setStaked] = useState(0);

  const basisCash = useBasisCash();
  const block = useBlockNumber();

  const fetchStaked = useCallback(
    async (address = basisCash?.myAccount) => {
      const staked = await basisCash.getStaked(mine.depositToken, address);
      setStaked(staked);
      return staked;
    },
    [basisCash?.myAccount, mine]
  );
  const fetchEarned = useCallback(
    async (address = basisCash?.myAccount) => {
      const earned = await basisCash.getEarned(poolName, address, pid)
      // console.log('ðŸš€ ~ file: useMineInfo.ts ~ line 29 ~ earned', earned)
      setEarned(earned)
      return earned
    },
    [basisCash?.myAccount, pid]
  )

  const fetchInfo = useCallback(async () => {
    fetchStaked();
  }, [basisCash?.myAccount, mine]);

  useEffect(() => {
    if (basisCash?.myAccount) {
      fetchInfo();
    }
  }, [basisCash?.myAccount, block, mine]);

  return { staked };
};

export default useMineInfo;
