//@ts-nocheck
import React, { useCallback, useEffect, useMemo, useState } from "react";
import { useTranslation } from "react-i18next";
import { useParams } from "react-router-dom";
import useIsMobile from "../../hooks/useIsMobile";
import { getNumberToMax, getNumberToAll, getDisplayNumber } from '../../utils/formatBalance'
import Back from "../../components/Back";
import Info from "./components/Info";
import Harvest from "./components/Harvest";
import Stake from "./components/Stake";
import BtnLink from "../../components/BtnLink";
import Spacer from "../../components/Spacer";
import useMine from "../../hooks/useMine";
import useMineInfo from "../../hooks/useMineInfo";
import HandlerModal from "../../components/HandlerModal";
const Mine: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false)
  const isMobile = useIsMobile();
  const { t } = useTranslation();
  const { mineId } = useParams();
  const mine = useMine(mineId);
  const mineInfo = useMineInfo(mine);
  const displayStakeBalance = useMemo(() => getNumberToMax(mineInfo.staked), [mineInfo.staked])
  const displayStakeBalance1 = useMemo(() => getNumberToAll(mineInfo.staked), [mineInfo.staked])

  return (
    <>
      <Back
        text={`${mine?.name} LP ${t("kuangchi")} `}
        img={mine.icon1}
        img1={mine.icon2}
      />
      <Info  mine={mine} mineInfo={mineInfo}/>
      <div className={` ${isMobile ? "" : "flex-jc-center width-100"} `}>
        <Harvest mine={mine} mineInfo={mineInfo}/>
        {isMobile ? <Spacer size="mmd" /> : null}
        <Stake mine={mine} mineInfo={mineInfo} staked={displayStakeBalance}   staked1={displayStakeBalance1}/>
      </div>
      <BtnLink
        text={` ${t("hq")} ${mine.depositTokenName}`}
        path={`/itank/detail/${mine.depositContract}`}
      />
      {/* <HandlerModal
          isOpen={isOpen}
      /> */}
    </>
  );
};

export default Mine;
